import React from 'react'
import Listitem from '../listItems/list_information'
import ap from './lists.module.css'

export default function Lists({todos, Removed, setTodos, editMode, value, setValue, Add, setEditMode}) {

function KeyDown(evt){
  if(evt.key === "Enter"){
    Add()
  }
}

  return (
    <div className={ap.box}>
        <div className={ap.todo_App}>
    {
      !todos.length ? <div className={ap.createItem_box}><p className={ap.createItem}>Please Create Item</p></div> :  <Listitem todos={todos} Removed={Removed} onChange={(newTodo) => {
        setTodos(todos.map((todo) => {
          if (todo.id === newTodo.id) {
            return newTodo
          } else {
            return todo
          }
        }))
      }} />
    }

        </div>
        {
          editMode ? <div>
            <input type="text" className={ap.input} value={value} autoFocus="autoFocus" onKeyDown={KeyDown} onChange={(val) => {
              setValue(val.target.value)
            }} />
            <button className='add_b' onClick={Add}>Add</button>
          </div>
            :
            <div className={ap.itemCheck_create}>
              <div className={ap.itemCheck}><span className={ap.plus} onClick={()=>{
                setEditMode(true)
              }}>+</span></div>
              <p>Create New Item</p>
            </div>
        }

      </div>
  )
}
