import React, { useState } from 'react';
import { Calendar } from 'react-calendar';
import './App.css'
import 'react-calendar/dist/Calendar.css';
import ChooseType from './chooseType/Choosetype';
import Lists from './lists/Lists';
import { Routes, Route, NavLink } from 'react-router-dom';


function App() {

  const [date, setDate] = useState(new Date());

  const [editMode, setEditMode] = useState(false)

  const [showCalendar, setShowCalendar] = useState(false)

  const [value, setValue] = useState("")

  const [completedTodos, setCompletedTodos] = useState([])
  const [inProgressTodos, setInProgressTodos] = useState([])
  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Write code for list",
      isCompleted: false,
      currentTime: new Date().toLocaleString()
    },
    {
      id: 2,
      text: "Read about REDUX",
      isCompleted: false,
      currentTime: new Date().toLocaleString()

    },
    {
      id: 3,
      text: "Repeat all the old",
      isCompleted: false,
      currentTime: new Date().toLocaleString()
    }
  ]);





  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false,
          currentTime: new Date().toLocaleString()
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }


  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))
    )
  }


  function KeyDown(evt) {
    if (evt.key === "Enter") {
      setEditMode(true)
    }
  }






  return (
    <div className='todo' tabIndex={0} onKeyDown={KeyDown} >
      <div className='container'>
        <div className='wrapper'>
          {
            showCalendar
              ?
              <div className='calendar'>
                <div >
                  <Calendar
                    onChange={setDate}
                    value={date}
                    selectRange={true}
                  />
                </div>
                {date.length > 0 ? (
                  <p >
                    <span>Start:</span>{' '}
                    {date[0].toDateString()}
                    &nbsp;|&nbsp;
                    <span >End:</span> {date[1].toDateString()}
                  </p>
                ) : (
                  <p className='text-center'>
                    <span >Default selected date:</span>{' '}
                    {date.toDateString()}
                  </p>
                )}
              </div>



              :

              <React.Fragment>
                <div className='header'>
                  <div className='day'>
                    <div className='iconBox' >

                    </div>
                    <NavLink to='' >

                    <div className='title' >Today</div>
                    </NavLink>
                  </div>
                  <ChooseType
                    completedTodos={completedTodos}
                    setCompletedTodos={setCompletedTodos}
                    setInProgressTodos={setInProgressTodos}
                    inProgressTodos={inProgressTodos}
                    setTodos={setTodos} todos={todos}
                  />
                </div>
                <Lists todos={todos}
                  Removed={Removed}
                  setTodos={setTodos}
                  editMode={editMode}
                  value={value}
                  setValue={setValue}
                  Add={Add}
                  setEditMode={setEditMode}
                  completedTodos={completedTodos}
                  setCompletedTodos={setCompletedTodos}
                  setInProgressTodos={setInProgressTodos}
                  inProgressTodos={inProgressTodos}
                />
              </React.Fragment>
          }
        </div>
      </div>
    </div>


  )
}

export default App;
