import React from 'react'
import { Routes, Route } from 'react-router-dom'
import InputCreate from '../InputCreate/InputCreate'
import Listitem from './listItems/ListItem'
import ap from './lists.module.css'

const aaa = () => {

}


export default function Lists({ setCompletedTodos, completedTodos, setInProgressTodos, inProgressTodos, todos, Removed, setTodos, editMode, value, setValue, Add, setEditMode }) {


  const [warning, setWarning] = React.useState(false)



  function KeyDown(evt) {
    if (evt.key === "Enter") {
      Add()
      setWarning(false)
    }
  }

  return (
    <div className={ap.box}>


      <div className={ap.todo_App}>
        <Routes>
          <Route exact path='/' element={<div>{
            !todos.length ? <div className={ap.createItem_box}><p className={ap.createItem}>Please Create Item</p></div> :
              <Listitem todos={todos} Removed={Removed} onChange={(newTodo) => {
                setTodos(todos.map((todo) => {
                  if (todo.id === newTodo.id) {
                    return newTodo
                  } else {
                    return todo
                  }
                }))
              }} />
          }</div>} />

          <Route exact path='/completed' element={<div>{

            // {  todos.map((item) => {
            //     if(item.isCompleted){
            //       setCompletedTodos([...completedTodos, item])
            //     }
            //   })}



            !completedTodos.length ? <div className={ap.createItem_box}><p className={ap.createItem}>You do not have any completed Tasks</p></div> :
              <Listitem todos={completedTodos} Removed={Removed} onChange={(newTodo) => {
                setTodos(todos.map((todo) => {
                  if (todo.id === newTodo.id) {
                    return newTodo
                  } else {
                    return todo
                  }
                }))
              }} />
          }</div>} />
          <Route exact path='/inProgress' element={<div>{



            !inProgressTodos.length ? <div className={ap.createItem_box}><p className={ap.createItem}>You do not have any completed Tasks</p></div> :
              <Listitem todos={inProgressTodos} Removed={Removed} onChange={(newTodo) => {
                setTodos(todos.map((todo) => {
                  if (todo.id === newTodo.id) {
                    return newTodo
                  } else {
                    return todo
                  }
                }))
              }} />
          }</div>} />
        </Routes>

      </div>
      <InputCreate editMode={editMode}
        setValue={setValue}
        setEditMode={setEditMode}
        value={value}
        KeyDown={KeyDown}
        Add={Add}
        warning={warning}
        setWarning={setWarning}
      />

    </div>
  )
}
