import React, { useState } from 'react';
import { Calendar } from 'react-calendar';
import './App.css'
// import 'react-calendar/dist/Calendar.css';
import ChooseType from './chooseType/Choosetype';
import Lists from './lists/Lists';


function App() {

  const [date, setDate] = useState(new Date());
  const [editMode, setEditMode] = useState(false)

  const [showCalendar, setShowCalendar] = useState(true)

  const [value, setValue] = useState("")


  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Write code for list",
      isCompleted: false
    },
    {
      id: 2,
      text: "Read about REDUX",
      isCompleted: false

    },
    {
      id: 3,
      text: "Repeat all the old",
      isCompleted: false
    }
  ]);
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }

  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))
    )
  }


  function KeyDown(evt) {
    if (evt.key === "Enter") {
      setEditMode(true)
    }
  }





  return (
    <div className='todo' tabIndex={0} onKeyDown={KeyDown} >
      <div className='container'>
        <div className='wrapper'>
          {
            showCalendar

              ?


              <div className='calendar'>
                <div >
                  <Calendar
                    onChange={setDate}
                    value={date}
                    selectRange={true}
                  />
                </div>
                {date.length > 0 ? (
                  <p >
                    <span>Start:</span>{' '}
                    {date[0].toDateString()}
                    &nbsp;|&nbsp;
                    <span >End:</span> {date[1].toDateString()}
                  </p>
                ) : (
                  <p className='text-center'>
                    <span >Default selected date:</span>{' '}
                    {date.toDateString()}
                  </p>
                )}
              </div>



              :

              <React.Fragment>
                <div className='header'>
                  <div className='day'>
                    <div className='iconBox' >


                      {/* <div className='dayCalendar'>day</div>
                <div className='today_Icon' >23</div> */}
                    </div>
                    <div className='title' >Today</div>
                  </div>
                  <ChooseType setTodos={setTodos} todos={todos} />
                </div>
                <Lists todos={todos}
                  Removed={Removed}
                  setTodos={setTodos}
                  editMode={editMode}
                  value={value}
                  setValue={setValue}
                  Add={Add}
                  setEditMode={setEditMode}
                />
              </React.Fragment>
          }
        </div>
      </div>
    </div>


  )
}

export default App;
