import { toHaveDescription } from '@testing-library/jest-dom/dist/matchers'
import React, { useState } from 'react'
import './ChooseType.css'






export default function ChooseType({ setTodos, todos }) {

  const [editMode, setEditMode] = useState(true)

  function Completed() {
    let passed = 0
    todos.map((todo) => {
      if (todo.isCompleted === true) {
        passed += 1
      }
    })
    if (passed > 0) {
      setTodos(todos.filter((todo) =>
        todo.isCompleted,

      ))
    }

  }
  function InProgress() {
    let notPassed = 0
    todos.map((todo) => {
      if (todo.isCompleted === false) {
        notPassed += 1
      }
    })
    if (notPassed > 0) {
      setTodos(todos.filter((todo) =>
        !todo.isCompleted,

      ))
    }

  }

  function Removed() {
setTodos(todos.filter((todo)=>todo.id === Number))
  }


  return (
    <div>
      {
        editMode ?
        <div className='dots' onClick={() => setEditMode(!editMode)}>
        <div className='dots_box'>
          <div className='dot'></div>
          <div className='dot'></div>
          <div className='dot'></div>
        </div>
      </div>
      :
<div className='dots' onClick={() => setEditMode(!editMode)}>
        <div className='dots_box'>
          <div className='dot'></div>
          <div className='dot'></div>
          <div className='dot'></div>
        </div>
      </div>

      }

      {
        editMode && <div>
          <div className='comands'>
            <div className='command_box'>
              <div onClick={Completed}>Completed</div>
              <div onClick={InProgress}>in Progress</div>
              <div  onClick={Removed}>Removed</div>
            </div>
          </div>
        </div>
      }
    </div>
  )
}
