import React from 'react'
import ap from './InputCreate.module.css'

export default function InputCreate({ editMode, setValue, setEditMode, value, KeyDown, Add }) {

    const [state, setState] = React.useState(false)


    return (
        <div>
            {
                editMode ? <div>
                    {state && 
                        <div className='Worr'>Characters must not exceed 35</div>
                    }
                    <input className={ap.input} type="text" value={value} autoFocus="autoFocus" onKeyDown={KeyDown} placeholder=" ..." onChange={(val) => {

                        if (val.target.value.length < 35) {
                            setValue(val.target.value)
                            setState(false)
                        }else{
                            setState(true)
                        }

                    }} />
                    <button className={ap.add_btn} onClick={Add}>Add</button>
                </div>
                    :
                    <div className={ap.itemCheck_create}>
                        <div className={ap.itemCheck}><span className={ap.plus} onClick={() => {
                            setEditMode(true)
                        }}>+</span></div>
                        <p>Create New Item</p>
                    </div>
            }
        </div>
    )
}
