import React, { useState } from 'react';
import { Calendar } from 'react-calendar';
import './App.css'
import 'react-calendar/dist/Calendar.css';
import ChooseType from './chooseType/Choosetype';
import Lists from './lists/Lists';
// import {Routes, Route} from 'react-router-dom';


function App() {

  const [date, setDate] = useState(new Date());

  const [editMode, setEditMode] = useState(false)

  const [showCalendar, setShowCalendar] = useState(false)

  const [value, setValue] = useState("")


  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Write code for list",
      isCompleted: false,
      currentTime: new Date().toLocaleString()
    },
    {
      id: 2,
      text: "Read about REDUX",
      isCompleted: false,
      currentTime: new Date().toLocaleString()

    },
    {
      id: 3,
      text: "Repeat all the old",
      isCompleted: false,
      currentTime: new Date().toDateString()
    }
  ]);
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false,
          currentTime: new Date().toDateString()
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }
console.log(new Date())

  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))
    )
  }


  function KeyDown(evt) {
    if (evt.key === "Enter") {
      setEditMode(true)
    }
  }






  return (
    <div className='todo' tabIndex={0} onKeyDown={KeyDown} >
      <div className='container'>
        <div className='wrapper'>
          {
            showCalendar

              ?

              <div className='calendar'>
                <div >
                  <Calendar
                    onChange={setDate}
                    value={date}
                    selectRange={true}
                  />
                </div>
                {date.length > 0 ? (
                  <p >
                    <span>Start:</span>{' '}
                    {todos.currentTime.toDateString()}
                    &nbsp;|&nbsp;
                    <span >End:</span> {todos.currentTime.toDateString()}
                  </p>
                ) : (
                  <p className='text-center'>
                    <span >Default selected date:</span>{' '}
                    {todos.currentTime.toDateString()}
                  </p>
                )}
              </div>



              :

              <React.Fragment>

                  <div className='day'>
                    <div className='iconBox'  onClick={()=>{
                    return (
                      <div>
                       {setShowCalendar(!showCalendar)}
                      </div>
                    )
                    }}>
                      <div className='dayCalendar'>day</div>
                      <div className='today_Icon' >23</div>
                    </div>
                    <div className='title' >Today</div>
                  </div>
                  <ChooseType setTodos={setTodos} todos={todos} />

                <Lists todos={todos}
                  Removed={Removed}
                  setTodos={setTodos}
                  editMode={editMode}
                  value={value}
                  setValue={setValue}
                  Add={Add}
                  setEditMode={setEditMode}
                />
              </React.Fragment>
          }
        </div>
      </div>
    </div>


  )
}

export default App;
