import React, { useState } from 'react'
import './ChooseType.css'






export default function ChooseType({ setTodos, todos, setCompleted, setInProgress, lists }) {

  const [editMode, setEditMode] = useState(false)

  function Completed() {
    let passed = 0
    todos.map((todo) => {
      if (todo.isCompleted === true) {
        passed += 1
      }
    })
    if (passed > 0) {
      setCompleted(todos.filter((todo) =>
        todo.isCompleted,
      ))
      lists = Completed
    }

  }
  function InProgress() {
    let notPassed = 0
    todos.map((todo) => {
      if (todo.isCompleted === false) {
        notPassed += 1
      }
    })
    if (notPassed > 0) {
      setInProgress(todos.filter((todo) =>
        !todo.isCompleted,

      ))
    }

  }

  function Removed() {

    setTodos(todos.filter((todo) => todo.id === Number))

  }


  return (
    <div>
      <div className='dots' onClick={() => setEditMode(!editMode)}>
        <div className={editMode ? "dots_box" : "dots_box1"}>
          <div className='dot'></div>
          <div className='dot'></div>
          <div className='dot'></div>
        </div>
      </div>
      {
        editMode &&
        <div>
          <div className='comands'>
            <div className='command_box'>
              <div onClick={Completed}>Completed</div>
              <span />
              <div onClick={InProgress}>in Progress</div>
              <span />
              <div onClick={Removed}>Removed</div>
            </div>
          </div>
        </div>
      }
    </div>
  )
}
