import React, { useState } from 'react';
import './App.css'
import ChooseType from './chooseType/Choosetype';
import Listitem from './listItems/list_information';
import Lists from './lists/Lists';


function App() {

  const [editMode, setEditMode] = useState(false)


  const [value, setValue] = useState("")

  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Nod JS",
      isCompleted: false
    },
    {
      id: 2,
      text: "Work",
      isCompleted: false

    },
    {
      id: 3,
      text: "Nod JS",
      isCompleted: false
    }
  ])
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }

  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))
    )
  }


  return (
    <div>
      <div className='wrapper'>
        <Lists todos={todos}
          Removed={Removed}
          setTodos={setTodos}
          editMode={editMode}
          value={value}
          setValue={setValue}
          Add={Add}
          setEditMode={setEditMode}
        />
        <ChooseType />
      </div>
    </div>


  )
}

export default App;
