import { faCalendarCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useState } from 'react';
import './App.css'
import ChooseType from './chooseType/Choosetype';
import Lists from './lists/Lists';


function App() {

  const [editMode, setEditMode] = useState(false)


  const [value, setValue] = useState("")

  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Nod JS",
      isCompleted: false
    },
    {
      id: 2,
      text: "Work",
      isCompleted: false

    },
    {
      id: 3,
      text: "Nod JS",
      isCompleted: false
    }
  ])
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }

  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))
    )
  }


  function KeyDown(evt) {
    if(evt.key === "Enter"){
      setEditMode(true)
    }
  }

  return (
    <div className='todo' aut tabIndex={0} onKeyDown={KeyDown} >
      <div className='container'>
        <div className='wrapper'>
          <div className='header'>
            <div className='day'>
              <FontAwesomeIcon icon={faCalendarCheck} />
              <div className='title' >Today</div>
            </div>
            <ChooseType setTodos={setTodos} todos={todos} />
          </div>
          <Lists todos={todos}
            Removed={Removed}
            setTodos={setTodos}
            editMode={editMode}
            value={value}
            setValue={setValue}
            Add={Add}
            setEditMode={setEditMode}
          />
        </div>
      </div>
    </div>


  )
}

export default App;
