import React from 'react'
import Listitem from '../listItems/list_information'
import ap from './lists.module.css'

export default function Lists({todos, Removed, setTodos, editMode, value, setValue, Add, setEditMode}) {
  return (
    <div className={ap.box}>
        <div className={ap.todo_App}>
          <Listitem todos={todos} Removed={Removed} onChange={(newTodo) => {
            setTodos(todos.map((todo) => {
              if (todo.id === newTodo.id) {
                return newTodo
              } else {
                return todo
              }
            }))
          }} />
        </div>
        {
          editMode ? <div>
            <input value={value} autoFocus="" onChange={(val) => {
              setValue(val.target.value)
            }} />
            <button onClick={Add}>Add</button>
          </div>
            :
            <div className={ap.itemCheck_create}>
              <div className={ap.itemCheck}><span className={ap.plus} onClick={()=>{
                setEditMode(true)
              }}>+</span></div>
              <p>Create New Item</p>
            </div>
        }

      </div>
  )
}
