import React, { useState } from 'react'
import './ChooseType.css'
import { NavLink } from 'react-router-dom'


export default function ChooseType({ completedTodos,  setCompletedTodos,  setInProgressTodos,  inProgressTodos,  setTodos, todos }) {

  const [editMode, setEditMode] = useState(false)




  function Removed() {

    setTodos(todos.filter((todo) => todo.id === Number))

  }


  const filterCompleted = () => {
    {
      todos.map((item) => {
        if(item.isCompleted){
          setCompletedTodos([...completedTodos, item])
        }
      })
    }
  }
  const filterInProgress = () => {
    {
      todos.map((item) => {
        if(item.isCompleted === false){
          setCo([...completedTodos, item])
        }
      })
    }
  }

  return (
    <div>
      <div className='dots' onClick={() => setEditMode(!editMode)}>
        <div className={editMode ? "dots_box" : "dots_box1"}>
          <div className='dot'></div>
          <div className='dot'></div>
          <div className='dot'></div>
        </div>
      </div>
      {
        editMode &&
        <div>
          <div className='comands'>
            <div className='command_box'>
              <NavLink to='/completed' onClick={filterCompleted} >Completed</NavLink>
              <span />
              <NavLink to='/inProgress' >in Progress</NavLink>
              <span />
              <div onClick={Removed}>Removed</div>
            </div>
          </div>
        </div>
      }
    </div>
  )
}
