import React, { useState } from 'react';
import './App.css'
import Listitem from './listItems/list_information';


function App() {


  const [value, setValue] = useState("")

  const [todos, setTodos] = useState([
    {
      id: 1,
      text: "Nod JS",
      isCompleted: false
    },
    {
      id: 2,
      text: "Work",
      isCompleted: false

    },
    {
      id: 3,
      text: "Nod JS",
      isCompleted: false
    }
  ])
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: todos.length + 1,
          text: value,
          isCompleted: false
        }
      ]),
      setValue("")
    )

  }

  function Removed(mer) {
    //  alert(mer)
    return (
      setTodos(todos.filter((a) => 
        a.id !== mer
      ))
    )
  }


  return (
    <div className='wrapper'>
      <div className='box'>
        <div className="todo_App">
          <Listitem todos={todos} Removed={Removed} onChange={(newTodo) => {
            setTodos(todos.map((todo) => {
              if (todo.id === newTodo.id) {
                return newTodo
              } else {
                return todo
              }
            }))
          }} />
        </div>
        <input value={value} onChange={(val) => {
          setValue(val.target.value)
        }} />
        <button onClick={Add}>Add</button>
      </div>
    </div>


  )
}

export default App;
