import it from './ListItem.module.css'
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Draggable, Droppable } from 'react-beautiful-dnd';


function Listitem({ todos, onChange, Removed }) {

    return (
        <Droppable droppableId="droppable-1" >
            {(provided, _) => (
                <div className={it.mapValue}
                    ref={provided.innerRef}
                    {...provided.droppableProps}

                >

                    {todos.map((todo, i) => {

                        return (
                            <div key={todo.id} >
                                <Draggable draggableId={"draggable-" + todo.id} index={i}>

                                    {(provided, snapshot) => (
                                        <div {...provided.dragHandleProps} className={it.todo_item}
                                            ref={provided.innerRef}

                                            style={{
                                                ...provided.draggableProps.style,
                                                boxShadow: snapshot.isDragging ? "0 0 .4rem #666" : "none"
                                            }}
                                            key={todo.id} onClick={() => {
                                                onChange({
                                                    ...todo,
                                                    isCompleted: !todo.isCompleted,
                                                })
                                            }} >
                                            <div className={it.leftItem}  >
                                                <div className={`${it.check_Btn} ${todo.isCompleted && it.checked_Btn}`} >
                                                    <FontAwesomeIcon className={it.check_Icon} icon={faCheck}   />
                                                </div>
                                                <div className={`${it.status} ${todo.isCompleted && it.completed}`} {...provided.draggableProps} >
                                                    {todo.text}
                                                </div>
                                                <div className={it.data} >
                                                    {todo.currentTime}
                                                </div>
                                            </div>
                                        </div>

                                    )}
                                </Draggable>

                            </div>
                        )
                    })}
                    {provided.placeholder}
                </div>
            )}

        </Droppable>
    )
}
export default Listitem
