import React from 'react'
import { Routes, Route } from 'react-router-dom'
import InputCreate from '../InputCreate/InputCreate'
import Listitem from './listItems/ListItem'
import ap from './lists.module.css'




export default function Lists({ todos, Removed, setTodos, editMode, value, setValue, Add, setEditMode, inProgress, completed }) {


  const [warning, setWarning] = React.useState(false)



  function KeyDown(evt) {
    if (evt.key === "Enter") {
      Add()
      setWarning(false)
    }
  }

  return (
    <div className={ap.box}>

      {/* <Route path='/all' element={<div></div>} />  */}

      <div className={ap.todo_App}>
        {
          !todos.length ? <div className={ap.createItem_box}><p className={ap.createItem}>Please Create Item</p></div> :
            <Routes>
              <Route path='' element={<Listitem todos={todos} Removed={Removed} onChange={(newTodo) => {
                setTodos(todos.map((todo) => {
                  if (todo.id === newTodo.id) {
                    return newTodo
                  } else {
                    return todo
                  }
                }))
              }} />} />

              <Route path='/completed' element={<></>} />

              <Route path='/inProgress' element={<Listitem todos={inProgress} Removed={Removed} onChange={(newTodo) => {
                setTodos(todos.map((todo) => {
                  if (todo.id === newTodo.id) {
                    return newTodo
                  } else {
                    return todo
                  }
                }))
              }} />} />

            </Routes>
        }
      </div> 
      {/* <Routes>


        </Routes> */}
    </div>
  )
}
