import React, { useEffect, useState } from 'react';
import './App.css'
import 'react-calendar/dist/Calendar.css';
import ChooseType from './Components/chooseType/Choosetype';
import Lists from './Components/lists/Lists';
import { DragDropContext } from 'react-beautiful-dnd';
import 'react-datepicker/dist'


function App() {

    const todoItem = JSON.parse(localStorage.getItem("todo")) ||
        [
            {
                id: 1,
                text: "Write code for list",
                isCompleted: false,
                currentTime: todoDat()
            },
            {
                id: 2,
                text: "Read about REDUX",
                isCompleted: false,
                currentTime: "2022-09-10"

            },
            {
                id: 3,
                text: "Repeat all the old",
                isCompleted: false,
                currentTime: todoDat()
            }
        ]



    const [editMode, setEditMode] = useState(false)

    const [value, setValue] = useState("")

    const [todos, setTodos] = useState(todoItem);

    const [completed, setCompleted] = useState([])

    const [inProgress, setInProgress] = useState([])

    const [visibleCalendar, setVisibleCalendar] = useState(false)

    const [dateValue, setDateValue] = useState()

    React.useEffect(() => {
        window.localStorage.setItem("todo", JSON.stringify(todos))
    }, [todos])

    function itemTransfer() {
        window.localStorage.setItem("todo", JSON.stringify(todos))
    }


    function Add() {
        if (value === '') {
            return
        }
        return (

            setTodos([
                ...todos,
                {
                    id: Math.floor(Math.random() * 1000),
                    text: value,
                    isCompleted: false,
                    currentTime: todoDat()

                }
            ]),
            setValue(""),
            setEditMode(false)
        )

    }

    function todoDat() {
        let today = new Date()
        let Month = today.getMonth() + 1
        let day = today.getDate()
        let year = today.getFullYear()
        return (year + "-0" + Month + "-0" + day)
    }




    function Removed(id) {

        return (

            setTodos(todos.filter((todo) =>

                todo.id !== id
            ))
        )
    }


    function KeyDown(evt) {
        if (evt.key === "Enter") {
            setEditMode(true)
        }
    }



    function Completed() {

        setCompleted(todos.filter((todo) =>

            todo.isCompleted,

        ))
        return setTodos

    }


    function InProgress() {

        setInProgress(todos.filter((todo) =>
            !todo.isCompleted,
        ))
    }





    useEffect(() => {
        Completed()
        InProgress()
    }, [todos])

    let today = new Date()
    let day = today.getDate()


    return (
        <div className='todo' tabIndex={0} onKeyDown={KeyDown} >
            <div className='container'>
                <div className='wrapper'>
                    <DragDropContext onDragEnd={(param) => {
                        const srcI = param.source.index;
                        const desI = param.destination?.index;
                        todos.splice(desI, 0, todos.splice(srcI, 1)[0])
                        itemTransfer()
                    }} >
                        <React.Fragment>
                            <div className='header'>
                                <div className='day'>
                                    <div className='iconBox' >
                                        {visibleCalendar &&
                                            <div className='data_range'>

                                            </div>
                                        }

                                        {!visibleCalendar && <div onClick={() => {
                                            setVisibleCalendar(!visibleCalendar)
                                        }}>
                                            <div className='dayCalendar'>Day</div>
                                            <div className='today_Icon' >{day}</div>
                                        </div>}

                                    </div>

                                    <div className='title'>today</div>

                                </div>
                                <ChooseType
                                    Completed={Completed}
                                    InProgress={InProgress}
                                    setTodos={setTodos}
                                    todos={todos}
                                    setCompleted={setCompleted}
                                    setInProgress={setInProgress}
                                    setDateValue={setDateValue}
                                    setVisibleCalendar={setVisibleCalendar}
                                />
                            </div>

                            <Lists
                                dateValue={dateValue}
                                inProgress={inProgress}
                                completed={completed}
                                todos={todos}
                                Removed={Removed}
                                setTodos={setTodos}
                                editMode={editMode}
                                value={value}
                                setValue={setValue}
                                Add={Add}
                                setEditMode={setEditMode}
                            />
                        </React.Fragment>
                    </DragDropContext>
                </div>
            </div>
        </div>


    )
}

export default App;
