import it from './ListItem.module.css'
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';


function Listitem({ todos, onChange, Removed }) {

    return (

        <div className={it.mapValue} >
            <DragDropContext onDragEnd={(...props) => {
                console.log(props)
            }}>
                <Droppable droppableId="droppable-1">
                    {(provided, _) => (
                        <div ref={provided.innerRef}  {...provided.droppableProps}>
                            {todos.map((todo, i) => {
                                return (
                                    <Draggable key={todo.id} graggableId={"graggable-" + todo.id} index={i}>
                                        {(provided, snapshot) => (
                                            <label className={it.todo_item} key={todo.id} htmlFor={todo.id}  ref={provided.innerRef} >
                                                <div className={it.leftItem}>
                                                    <input
                                                        id={todo.id} className={it.checkbox} type="checkbox"
                                                        checked={todo.isCompleted} onChange={() => {
                                                            onChange({
                                                                ...todo,
                                                                isCompleted: !todo.isCompleted
                                                            })
                                                        }}
                                                    />
                                                    <label className={it.check} htmlFor={todo.id}>
                                                        <div className={it.chek_Btn}>
                                                            {
                                                                todo.isCompleted ?
                                                                    <FontAwesomeIcon className={it.check_Icon} icon={faCheck} /> :
                                                                    <div></div>
                                                            }

                                                        </div>
                                                    </label>
                                                    <div className={it.field} >
                                                        {
                                                            todo.isCompleted ?
                                                                <i><s>{todo.text}</s></i> : <>{todo.text}</>
                                                        }
                                                        <div className={it.data} >
                                                            {
                                                                todo.currentTime
                                                            }
                                                        </div>

                                                    </div>

                                                </div>
                                                <div className={it.removeBut} onClick={() => {
                                                    Removed(todo.id)
                                                }}><p className={it.remove} >x</p>
                                                </div>

                                            </label>
                                        )}

                                    </Draggable>
                                )
                            })}
                        </div>
                    )}

                </Droppable>
            </DragDropContext>
        </div>
    )
}
export default Listitem
