import it from './ListItem.module.css'
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';


function Listitem({ todos, onChange, Removed }) {

    return (

        <div className={it.mapValue} >
            <DragDropContext
                onDragEnd={(...props) => { console.log(props) }}>
                {todos.map((todo, i) => {

                    return (
                        <Droppable droppableId="droppable-1" key={todo.id}>
                            {(provided, _) => (
                                <div
                                    ref={provided.innerRef}
                                    {...provided.droppableProps}
                                    className={it.todo_item} onClick={() => {
                                        onChange({
                                            ...todo,
                                            isCompleted: !todo.isCompleted,
                                        })
                                    }} >
                                    <Draggable key={todo.id} draggableId={'draggable-' + todo.id} index={i}>
                                        {(provided, snapshot) => (
                                            <div className={it.leftItem} ref={provided.innerRef}
                                                {...provided.draggableProps}>

                                                <div className={`${it.check_Btn} ${todo.isCompleted && it.checked_Btn}`} >
                                                    <FontAwesomeIcon className={it.check_Icon} icon={faCheck} />
                                                </div>

                                                <div className={`${it.status} ${todo.isCompleted && it.completed}`}
                                                    {...provided.dragHandleProps}>
                                                    {todo.text}
                                                </div>
                                                <div className={it.data} >
                                                    {todo.currentTime}
                                                </div>
                                            </div>
                                        )}


                                        <div className={it.removeBut} onClick={() => {
                                            Removed(todo.id)
                                        }}><p className={it.remove} >x</p>
                                        </div>
                                    </Draggable>
                                </div>
                            )}

                        </Droppable>
                    )
                })}
            </DragDropContext>
        </div>

    )
}
export default Listitem
