import React, { useEffect, useState } from 'react';
import './App.css'
import 'react-calendar/dist/Calendar.css';
import ChooseType from './chooseType/Choosetype';
import Lists from './lists/Lists';
import ReactDatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css"

// import {Routes, Route} from 'react-router-dom';



function App() {


  const todoItem = JSON.parse(localStorage.getItem("todo")) ||
    [
      {
        id: 1,
        text: "Write code for list",
        isCompleted: false,
        currentTime: new Date().toLocaleString()
      },
      {
        id: 2,
        text: "Read about REDUX",
        isCompleted: false,
        currentTime: new Date().toLocaleString()

      },
      {
        id: 3,
        text: "Repeat all the old",
        isCompleted: false,
        currentTime: new Date().toLocaleString()
      }
    ]





  const [editMode, setEditMode] = useState(false)



  const [value, setValue] = useState("")

  const [todos, setTodos] = useState(todoItem);

  const [completed, setCompleted] = useState([])

  const [inProgress, setInProgress] = useState([])

  const [visibleCalendar, setVisibleCalendar] = useState(false)
  React.useEffect(() => {
    window.localStorage.setItem("todo", JSON.stringify(todos))
  })
  function Add() {
    if (value === '') {
      return
    }
    return (

      setTodos([
        ...todos,
        {
          id: Math.floor(Math.random() * 1000),
          text: value,
          isCompleted: false,
          currentTime: new Date().toLocaleString()
        }
      ]),
      setValue(""),
      setEditMode(false)
    )

  }


  function Removed(id) {
    return (
      setTodos(todos.filter((todo) =>
        todo.id !== id
      ))

    )
  }


  function KeyDown(evt) {
    if (evt.key === "Enter") {
      setEditMode(true)
    }
  }



  function Completed() {
    let passed = 0

    todos.map((todo) => {

      if (todo.isCompleted) {
        passed += 1
      }
      return todos
    })
    if (passed > 0) {
      setCompleted(todos.filter((todo) =>
        todo.isCompleted,
      ))
      return setTodos
    }


  }


  function InProgress() {
    let notPassed = 0
    todos.map((todo) => {
      if (!todo.isCompleted) {
        notPassed += 1
      }
      return todos
    })
    if (notPassed > 0) {
      setInProgress(todos.filter((todo) =>
        !todo.isCompleted,

      ))
    }

  }



  useEffect(() => {
    Completed()
    InProgress()
  }, [todos])

  const [dateStart, setDateStart] = useState();
  const [dateEnd, setDateEnd] = useState();

  function onChangeHandler(value) {
    setDateStart(value[0]);
    setDateEnd(value[1]);

  }

useEffect(() => {
  if(dateEnd){
    setVisibleCalendar(false)
  }
},[dateEnd])

{dateEnd && dateEnd.map(data => console.log(data))}


  return (
    <div className='todo' tabIndex={0} onKeyDown={KeyDown} >
      <div className='container'>
        <div className='wrapper'>
          <React.Fragment>
            <div className='header'>
              <div className='day'>
                <div className='iconBox' >
                  {visibleCalendar && <ReactDatePicker
                    id="dateStartEnd"
                    selectsRange={true}
                    startDate={dateStart}
                    endDate={dateEnd}
                    onChange={onChangeHandler}
                    dateFormat="dd MMM yyyy"
                    className={'form-control form-control-sm'}
                    showDisabledMonthNavigation
                  />}
                  {!visibleCalendar && <div onClick={() => {
                    setVisibleCalendar(!visibleCalendar)
                  }}>
                    <div className='dayCalendar'>day</div>
                    <div className='today_Icon' >23</div>
                  </div>}

                </div>

                <div className='title' ></div>
              </div>
              <ChooseType
                Completed={Completed}
                InProgress={InProgress}
                setTodos={setTodos}
                todos={todos}
                setCompleted={setCompleted}
                setInProgress={setInProgress}
              />
            </div>
            <Lists
              inProgress={inProgress}
              completed={completed}
              todos={todos}
              Removed={Removed}
              setTodos={setTodos}
              editMode={editMode}
              value={value}
              setValue={setValue}
              Add={Add}
              setEditMode={setEditMode}
            />
          </React.Fragment>
        </div>
      </div>
    </div>


  )
}

export default App;
