import it from './ListItem.module.css'
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";



function Listitem({ todos, onChange, Removed }) {

    return (

        <label className={it.mapValue} >

            {todos.map((todo) => {

                return (


                    <div className={it.todo_item} key={todo.id} htmlFor={todo.id} onClick= {(e) =>{
                        console.log(e)
                    }}>
                        <div className={it.leftItem}>
                            <input
                                id={todo.id} className={it.checkbox} type="checkbox"
                                checked={todo.isCompleted} onChange={() => {
                                    onChange({
                                        ...todo,
                                        isCompleted: !todo.isCompleted
                                    })
                                }}
                            />
                            <label className={it.check} htmlFor={todo.id}>
                                <div className={it.check_Btn}>
                                    {
                                        todo.isCompleted ?
                                            <FontAwesomeIcon className={it.check_Icon} icon={faCheck} /> :
                                            <div></div>
                                    }

                                </div>
                            </label>
                            <div className={it.field} >
                                <div>
                                    {
                                        todo.isCompleted ?
                                            <i><s>{todo.text}</s></i> : <>{todo.text}</>
                                    }
                                </div>
                                <div className={it.data} >
                                    {
                                        todo.currentTime
                                    }
                                </div>

                            </div>

                        </div>
                        <div className={it.removeBut} onClick={() => {
                            Removed(todo.id)
                        }}><p className={it.remove} >x</p>
                        </div>

                    </div>
                )
            })}
        </label>
    )
}
export default Listitem
